    private void btnCalcActionPerformed(java.awt.event.ActionEvent evt) {                                        
        DecimalFormat df = new DecimalFormat("#,##0.00");
        
        double valor, valorFinal;
        double dolar = 5.27;
        double euro = 5.44;
        double libra = 6.29;
        
        valor = Double.parseDouble(txtValorReal.getText());
        
        if(rbdDolar.isSelected()){
            valorFinal = valor / dolar;
    } else if(rbdEuro.isSelected()){
            valorFinal = valor / euro;
        }   else {
                valorFinal = valor / libra;
            }
        
        
        lblValorConv.setText("$ " + df.format(valorFinal));
    }                                       

